import { Component as AngularComponent, OnInit, OnDestroy } from '@angular/core';

import { environment } from '../environments/environment';
import { Cuss2Service, ICuss2ServiceOptions } from '@elevated-libs/cuss2-angular';
import {ApplicationStates, Cuss2, Component } from "@elevated-libs/cuss2";
import ApplicationStateCodeEnum = ApplicationStates.ApplicationStateCodeEnum;

@AngularComponent({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit, OnDestroy {
  title = 'cuss2-example-angular';
  private alive: boolean = true;
  cuss2?: Cuss2;
  assets: string = 'PT##$S6A#@;#TICK#CHEC#BOAR#0101110112011301210122012301C#0201A34#03BRB061661#0430G25F\n' +
    'TT01#01L08004790100000\n' +
    '';
  coupon: string = 'CP#A#01S#CP#C01#02@@01#03M1THIS IS A BARCODE#04THIS IS A BOARDING PASS#';
  connectionStatus = "Connecting to CUSS2 platform...";

  constructor(private cuss2Service: Cuss2Service){}

  async ngOnInit(): Promise<void> {
    this.cuss2Service.logger = true
    // TODO: make it so the config only has to be passed in on module creation
    try {
      this.cuss2 = await this.cuss2Service.start(environment.cuss2Config as ICuss2ServiceOptions);
      this.connectionStatus = '';
    }
    catch(e) {
      console.error(e);
      this.connectionStatus = e.message;
    }

    if (!this.cuss2) {
      return;
    }

    this.cuss2.stateChange.subscribe(async (state: ApplicationStateCodeEnum) => {
      this.title = state
    });

    Object.values(this.cuss2.components).forEach((c:any) => c.required = false);
    if (this.cuss2.bagTagPrinter) this.cuss2.bagTagPrinter.required = true;
    if (this.cuss2.boardingPassPrinter) this.cuss2.boardingPassPrinter.required = true;
    if (this.cuss2.barcodeReader) this.cuss2.barcodeReader.required = true;

    // @ts-ignore
    window.cuss2 = this.cuss2;


    this.cuss2.applicationOnline = true;

  }

  get components() : Component[] {
    return Object.values<Component>(this.cuss2?.components||{})
  }

  get online() : boolean {
    return Boolean(this.cuss2?.applicationOnline);
  }
  set online(v : boolean) {
    if (this.cuss2) this.cuss2.applicationOnline = v;
  }

  canExit() : boolean {
    const state = this.cuss2?.state;
    return state === ApplicationStateCodeEnum.ACTIVE
  }

  exit() : void {
    this.cuss2?.requestAvailableState();
  }

  print() : void {
    const assets = this.assets.split(/[\r\n]/g).filter(a => !!a.trim().length)
    console.log(assets)
    this.cuss2?.boardingPassPrinter?.setupAndPrintRaw(assets, this.coupon);
  }

  ngOnDestroy(): void {
    this.alive = false;
  }
}
